import { Component,OnInit } from '@angular/core';
import { FakestoreService } from 'src/app/services/fakestore.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {


  constructor(private fakestore:FakestoreService){}

  //declare a variable to hold the data
  products:any[]=[];

  ngOnInit():void{
    this.fakestore.getProduct().subscribe((products:any)=>{
      console.table(products);
      this.products=products;
    });

  }


}



<div class="container">
  <button class="btn btn-primary" routerLink="../create" type="submit">Create Product</button>
  <div class="row" ngFor="let product of products">
    <div class="card" style="width: 18rem">
          <img src={{product.image}} class="card-img-top" alt="...">
          <div class="card-body">
              <h5 class="card-title">{{product.title}}</h5>
              <p class="card-text">{{product.description}}</p>
              <a href="#" class="btn btn-primary">Go somewhere</a>
          </div>
      </div> 

  </div>
</div>
